import React, { useRef, useCallback, useEffect } from 'react';
import styled from 'styled-components';
import { LOAD_BOARDS_REQUEST } from '../reducers/board';
import { useSelector, useDispatch } from 'react-redux';
import BoardTable from '../components/BoardTable';
import Button from '../components/designs/Button';

const SBoard = styled.div`
  margin-top: 10px;
`;
const BoardDivTop = styled.div`
  display: flex;
  align-items: center;
`;

const SBoardTitle = styled.h1`
  display: inline-block;
  font-weight: bold;
  margin-left: 10px;
  width: 200px;
`;
const SearchForm = styled.form`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  padding: 0px 10px;

  width: 99%;

  & a {
    color: white;
    height: 50px;
    padding-right: 10px;
    display: flex;
    align-items: center;
    justify-content: center;
  }
`;

const SearchInput = styled.input`
  margin-right: 10px;
  border: solid 2px #000000;
  border-radius: 4px;
  padding-left: 10px;

  height: 2.25rem;
  width: 10rem;
  font-size: 1.5rem;

  transition: 0.55s ease;

  & :hover {
    width: 30rem;
  }
  & :focus {
    width: 30rem;
  }
`;

const PageButtonDiv = styled.div``;

const Board = () => {
  const { boards, hasMoreBoards } = useSelector(state => state.board);
  const dispatch = useDispatch();
  const countRef = useRef([]);

  const onScroll = useCallback(() => {
    if (
      window.scrollY + document.documentElement.clientHeight >
      document.documentElement.scrollHeight - 300
    ) {
      if (hasMoreBoards) {
        let lastId = boards[boards.length - 1].id;
        if (!countRef.current.includes(lastId)) {
          dispatch({
            type: LOAD_BOARDS_REQUEST,
            lastId,
          });
          countRef.current.push(lastId);
        }
      }
    }
  }, [hasMoreBoards, boards.length]);

  useEffect(() => {
    window.addEventListener('scroll', onScroll);
    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, [boards.length]);

  return (
    <SBoard>
      <BoardDivTop>
        <SBoardTitle>추천 게시판</SBoardTitle>
        <SearchForm>
          <SearchInput></SearchInput>
          <Button>검색</Button>
        </SearchForm>
      </BoardDivTop>
      {boards && <BoardTable board={boards} />}
      {boards && <PageButtonDiv></PageButtonDiv>}
    </SBoard>
  );
};

Board.getInitialProps = async context => {
  context.store.dispatch({
    type: LOAD_BOARDS_REQUEST,
  });
};

export default Board;
