import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';

import BookCard from '../components/BookCard';
import { LOAD_BOOKS_REQUEST } from '../reducers/book';

const BookListTitle = styled.h1`
  margin: 10px 0px 10px 0px;
  font-weight: bold;
`;

const NovelList = styled.div`
  width: 98%;
  overflow: hidden;
`;

const BookList = () => {
  const { books, hasMoreBooks, keyword } = useSelector(state => state.book);
  const dispatch = useDispatch();
  const countRef = useRef([]);

  const onScroll = useCallback(() => {
    if (
      window.scrollY + document.documentElement.clientHeight >
      document.documentElement.scrollHeight - 300
    ) {
      if (hasMoreBooks) {
        let lastId = books[books.length - 1].id;
        if (!countRef.current.includes(lastId)) {
          dispatch({
            type: LOAD_BOOKS_REQUEST,
            lastId,
            keyword,
          });
          countRef.current.push(lastId);
        }
      }
    }
  }, [hasMoreBooks, books.length, keyword]);

  useEffect(() => {
    window.addEventListener('scroll', onScroll);
    return () => {
      window.addEventListener('scroll', onScroll);
    };
  }, [books.length]);

  return (
    <div>
      <BookListTitle>연재소설 목록</BookListTitle>
      <NovelList>
        {books &&
          books.map(book => {
            return (
              <BookCard
                key={+book.id}
                book={book}
                nickname={book.User.nickname}
              />
            );
          })}
      </NovelList>
    </div>
  );
};

BookList.getInitialProps = async context => {
  const state = context.store.getState();
  context.store.dispatch({
    type: LOAD_BOOKS_REQUEST,
    lastId: 0,
    keyword: state.book.keyword,
  });
};

export default BookList;
