import {
  all,
  fork,
  takeEvery,
  takeLatest,
  call,
  put,
} from 'redux-saga/effects';
import axios from 'axios';
import {
  LOG_IN_REQUEST,
  LOG_IN_FAILURE,
  LOG_IN_SUCCESS,
  LOG_OUT_REQUEST,
  LOG_OUT_FAILURE,
  LOG_OUT_SUCCESS,
  SIGN_UP_REQUEST,
  SIGN_UP_FAILURE,
  SIGN_UP_SUCCESS,
} from '../reducers/user';

// 로그인
function* watchLogin() {
  yield takeLatest(LOG_IN_REQUEST, function* login(action) {
    try {
      const result = yield call(loginData => {
        return axios.post('/user/login', loginData, {
          withCredentials: true,
        });
      }, action.data);
      yield put({
        type: LOG_IN_SUCCESS,
        data: result.data,
      });
    } catch (e) {
      console.log(e);
      yield put({
        type: LOG_IN_FAILURE,
        error: e,
      });
    }
  });
}

// 로그아웃
function* watchLogout() {
  yield takeLatest(LOG_OUT_REQUEST, function* logout() {
    try {
      yield call(() => {
        return axios.post(
          '/user/logout',
          {},
          {
            withCredentials: true,
          },
        );
      });
      yield put({
        type: LOG_OUT_SUCCESS,
      });
    } catch (e) {
      console.log(e);
      yield put({
        type: LOG_OUT_FAILURE,
        error: e,
      });
    }
  });
}

// 회원가입
function* watchSignUp() {
  yield takeLatest(SIGN_UP_REQUEST, function* signUp(action) {
    try {
      const result = yield call(signUpData => {
        return axios.post('/user/signup', signUpData);
      }, action.data);
      yield put({
        type: SIGN_UP_SUCCESS,
        data: result,
      });
    } catch (e) {
      console.log(e);
      yield put({
        type: SIGN_UP_FAILURE,
        error: e,
      });
    }
  });
}

export default function* userSaga() {
  yield all([fork(watchSignUp), fork(watchLogin), fork(watchLogout)]);
}
