import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import Router from 'next/router';
import { useSelector } from 'react-redux';

import { LOAD_USER_REQUEST } from '../reducers/user';

const SProfileDiv = styled.div`
  width: 98%;
  margin-top: 10px;
`;
const ProfileTitle = styled.h1`
  display: inline-block;
  font-weight: bold;
  margin-left: 10px;
  width: 100%;
`;
const InfoCardWrap = styled.div`
  margin-top: 2rem;
  margin-left: 1.5rem;
  border: solid 1px #495057;
  border-radius: 6px;
  width: 70%;
`;
const InfoCardDiv = styled.div`
  display: flex;
  align-items: center;
  margin: 1rem;
  height: 4rem;
`;
const InfoLeftDiv = styled.div`
  flex: 1;
`;
const InfoRightDiv = styled.div`
  flex: 3;

  & > input {
    width: 100%;
    height: 2rem;
    font-size: 1.5rem;
    line-height: 2rem;
  }
`;

const Profile = ({ id }) => {
  const { me } = useSelector(state => state.user);

  useEffect(
    e => {
      if (!(me && me.id)) {
        alert('로그인 하지 않은 사용자는 접근 할 수 없습니다.');
        Router.push('/');
        return;
      }
      if ((me && me.userId) !== id) {
        alert('잘 못된 접근입니다.');
        Router.push('/');
        return;
      }
    },
    [me && me.userId, id],
  );

  return (
    <SProfileDiv>
      <ProfileTitle>{me && me.nickname} 님 프로필 수정</ProfileTitle>
      <InfoCardWrap>
        <InfoCardDiv>
          <InfoLeftDiv>
            <labe>아이디</labe>
          </InfoLeftDiv>
          <InfoRightDiv>
            <input value={me && me.userId} />
          </InfoRightDiv>
        </InfoCardDiv>
        <InfoCardDiv>
          <InfoLeftDiv>
            <label>비밀번호</label>
          </InfoLeftDiv>
          <InfoRightDiv>비밀번호변경</InfoRightDiv>
        </InfoCardDiv>
        <InfoCardDiv>
          <InfoLeftDiv>
            <label>닉네임</label>
          </InfoLeftDiv>
          <InfoRightDiv>
            <input value={me && me.nickname} />
          </InfoRightDiv>
        </InfoCardDiv>
      </InfoCardWrap>

      {/* <table>
        <tr>
          <td>아이디</td>
          <td>{me && me.userId}</td>
        </tr>
        <tr>
          <td>닉네임</td>
          <td>{me && me.nickname}</td>
        </tr>
        <tr>
          <td>연재한 글 수</td>
          <td>{me && me.Books.length}</td>
        </tr>
        <tr>
          <td>선호작 수</td>
          <td>{me && me.LikingBook.length}</td>
        </tr>
        <tr>
          <td>선호작가 수</td>
          <td>{me && me.LikingUser.length}</td>
        </tr>
      </table> */}
    </SProfileDiv>
  );
};

Profile.propTypes = {
  id: PropTypes.string.isRequired,
};

Profile.getInitialProps = async context => {
  const id = context.query.id;
  return { id };
};

export default Profile;
