import React, { useCallback } from 'react';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import Button from './designs/Button';

const Profile = styled.div`
  width: 90%;
  margin-top: 10px;
  margin-left: 10px;
  font-size: 1rem;
`;

const TopProfile = styled.div`
  display: flex;
  align-items: center;
  border-bottom: solid 2px #495057;
  height: 70px;
`;

const Nickname = styled.div`
  flex: 10;
`;

const MiddleProfile = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  border-bottom: solid 2px #495057;
  height: 100px;

  & > * {
    flex: 1;
  }
`;

const Preference = styled.div`
  & label {
    text-align: center;
  }
`;

const Label = styled.label`
  display: block;

  & + & {
    margin-top: 10px;
  }
`;

const BottomProfile = styled.div`
  width: 90%;

  & > * {
    margin-top: 10px;
  }
`;

const LikeBookList = styled.ul`
  display: block;
`;

const LikeBook = styled.li``;

const DummyLikeBook = [
  {
    writer: '글슬슬',
    bookname: '천재배우의 아우라',
  },
  {
    writer: ' 준슬',
    bookname: '시스템 에러로 종족초월',
  },
  {
    writer: '하이엔드',
    bookname: '천재의 게임방송',
  },
  {
    writer: '디다트',
    bookname: 'BJ대마도사',
  },
  {
    writer: '김강현',
    bookname: '천마는 조용히 살고싶다.',
  },
  {
    writer: '마교졸개',
    bookname: '회귀자가 다 뺏어먹음',
  },
  {
    writer: '근서',
    bookname: '내가 키운 S급들',
  },
  {
    writer: '판미손',
    bookname: '빌린어 방송을 너무 잘함',
  },
];

const UserProfile = () => {
  const dispatch = useDispatch();

  const onLogout = useCallback(() => {
    dispatch();
  }, []);

  return (
    <Profile>
      <TopProfile>
        <Nickname>돼지돼지</Nickname>
        <Button onClick={onLogout}>로그아웃</Button>
      </TopProfile>
      <MiddleProfile>
        <Preference>
          <Label>선호작품</Label>
          <Label>10</Label>
        </Preference>
        <Preference>
          <Label>선호작가</Label>
          <Label>20</Label>
        </Preference>
      </MiddleProfile>
      <BottomProfile>
        <Label>선호작 목록</Label>
        <LikeBookList>
          {DummyLikeBook.map((v, i) => {
            <LikeBook key={i}>{v.bookname}</LikeBook>;
          })}
        </LikeBookList>
      </BottomProfile>
    </Profile>
  );
};

export default UserProfile;
