import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import Helmet from 'react-helmet';

import { LOAD_BOOK_REQUEST } from '../../reducers/book';
import Button from '../../components/designs/Button';

const WriteEpisodeDiv = styled.div`
  width: 98%;
  margin: 1rem 0rem 0rem 1rem;
`;

const WriteEpisodeTopDiv = styled.div`
  position: relative;

  margin-bottom: 2rem;
`;

const BookTitle = styled.h1`
  font-weight: bold;
  font-size: 1rem;
  margin-bottom: 1rem;
`;

const TopButtonDiv = styled.div`
  position: absolute;
  top: 5%;
  right: 1%;
`;

const WriteEpisodeContentDiv = styled.div``;

const NoticeCheckDiv = styled.div`
  & > label {
    font-size: 1.25rem;
  }
`;

const WriteEditorDiv = styled.div``;

const Write = () => {
  const { book } = useSelector(state => state.book);

  const editorRef = useRef();
  const [editorLoded, setEditorLoded] = useState(false);
  const { CKEditor, ClassicEditor } = editorRef.current || {};

  const [text, setText] = useState('');

  useEffect(() => {
    editorRef.current = {
      CKEditor: require('@ckeditor/ckeditor5-react'),
      ClassicEditor: require('@ckeditor/ckeditor5-build-classic'),
    };
    setEditorLoded(true);
  }, []);

  return (
    <WriteEpisodeDiv>
      <Helmet>
        <title>{book && book.name}</title>
        <style type="text/css">
          {`
            .ck.ck-editor {width:100%;}
            .ck-editor__editable { height: 500px;}
          `}
        </style>
      </Helmet>
      <WriteEpisodeTopDiv>
        <BookTitle>{book && book.name}</BookTitle>
        <TopButtonDiv>
          <Button color="pink">취소</Button>
          <Button>작성</Button>
        </TopButtonDiv>
      </WriteEpisodeTopDiv>
      <WriteEpisodeContentDiv>
        <NoticeCheckDiv>
          <label>이 글을 공지로 작성</label>
          <input type="checkbox" />
        </NoticeCheckDiv>
      </WriteEpisodeContentDiv>
      <WriteEditorDiv>
        {editorLoded ? (
          <CKEditor
            editor={ClassicEditor}
            data={text}
            onChange={(event, editor) => {
              const data = editor.getData();
              setText(data);
            }}
          />
        ) : (
          <p>Editor Loding</p>
        )}
      </WriteEditorDiv>
    </WriteEpisodeDiv>
  );
};

Write.getInitialProps = async context => {
  const bookId = context.query.bookid;

  context.store.dispatch({
    type: LOAD_BOOK_REQUEST,
    id: bookId,
  });
};

export default Write;
