import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import Router from 'next/router';
import { useSelector } from 'react-redux';

import { LOAD_USER_REQUEST } from '../reducers/user';

const SProfileDiv = styled.div`
  width: 98%;
`;
const InfoCardDiv = styled.div`
  display: flex;
`;
const InfoLeftDiv = styled.div``;
const InfoRightDiv = styled.div``;

const Profile = ({ id }) => {
  const { me } = useSelector(state => state.user);

  useEffect(
    e => {
      if (!(me && me.id)) {
        alert('로그인 하지 않은 사용자는 접근 할 수 없습니다.');
        Router.push('/');
        return;
      }
      if ((me && me.userId) !== id) {
        alert('잘 못된 접근입니다.');
        Router.push('/');
        return;
      }
    },
    [me && me.userId, id],
  );

  return (
    <SProfileDiv>
      <InfoCardDiv>
        <InfoLeftDiv>아이디</InfoLeftDiv>
        <InfoRightDiv>{me && me.userId}</InfoRightDiv>
      </InfoCardDiv>
      <InfoCardDiv>
        <InfoLeftDiv>비밀번호</InfoLeftDiv>
        <InfoRightDiv>비밀번호변경</InfoRightDiv>
      </InfoCardDiv>
      <InfoCardDiv>
        <InfoLeftDiv>닉네임</InfoLeftDiv>
        <InfoRightDiv>{me && me.nickname}</InfoRightDiv>
      </InfoCardDiv>
      {/* <table>
        <tr>
          <td>아이디</td>
          <td>{me && me.userId}</td>
        </tr>
        <tr>
          <td>닉네임</td>
          <td>{me && me.nickname}</td>
        </tr>
        <tr>
          <td>연재한 글 수</td>
          <td>{me && me.Books.length}</td>
        </tr>
        <tr>
          <td>선호작 수</td>
          <td>{me && me.LikingBook.length}</td>
        </tr>
        <tr>
          <td>선호작가 수</td>
          <td>{me && me.LikingUser.length}</td>
        </tr>
      </table> */}
    </SProfileDiv>
  );
};

Profile.propTypes = {
  id: PropTypes.string.isRequired,
};

Profile.getInitialProps = async context => {
  const id = context.query.id;
  return { id };
};

export default Profile;
