import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import moment from 'moment';

const Card = styled.div`
  border: solid 1px #495057;
  /* width: 30rem; */
  display: inline-block;
  width: 30%;
  height: 15rem;

  margin: 10px 10px 10px 10px;
`;

const Thumbnail = styled.img`
  width: 10rem;
  height: 13rem;
  float: left;

  margin-top: 1rem;
  margin-left: 1rem;
`;

const CardContent = styled.div`
  width: 16rem;
  height: 9rem;
  float: right;
  margin-top: 3rem;
  margin-right: 1rem;

  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const Title = styled.label`
  display: block;
  font-weight: bold;
`;

const Genre = styled.label`
  display: block;
`;

const Writer = styled.label`
  display: block;
`;

const CreatedDate = styled.label`
  display: block;
`;

const BookCard = ({ book, nickname }) => {
  const dispatch = useDispatch();

  return (
    <Card>
      <Thumbnail src={book.thumbnail}></Thumbnail>
      <CardContent>
        <Title>{book.name}</Title>
        <Genre>{book.BookGenre[0].name}</Genre>
        <Writer>{nickname}</Writer>
        <CreatedDate>
          등록일 {moment(book.createdAt).format('YYYY.MM.DD')}
        </CreatedDate>
      </CardContent>
    </Card>
  );
};

BookCard.propTypes = {
  book: PropTypes.object.isRequired,
  nickname: PropTypes.string.isRequired,
};

export default BookCard;
