import React, { useRef, useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import Helmet from 'react-helmet';

import Button from '../components/designs/Button';
import Router from 'next/router';
import { useSelector, useDispatch } from 'react-redux';

const SWriteBookDiv = styled.div`
  width: 98%;
  margin: 1rem 0rem 0rem 1rem;
`;

const SWriteBookTitle = styled.h1`
  display: inline-block;
  font-weight: bold;
  margin-left: 10px;
  width: 100%;
`;

const FormBook = styled.form``;

const ButtonDiv = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-end;
`;

const DivCard = styled.div`
  display: flex;
  align-items: center;
  margin: 1rem;

  width: 80%;
`;
const DivCardLeft = styled.div`
  flex: 1;
`;
const DivCardRight = styled.div`
  flex: 4;
  display: flex;

  & > input {
    width: 100%;
    height: 2rem;
    font-size: 1rem;
    line-height: 2rem;
    padding-left: 10px;

    border: solid 2px #495057;
    border-radius: 4px;

    transition: 0.55s ease;
    & :hover {
      border: solid 2px #148cff;
    }
    & :focus {
      border: solid 2px #148cff;
    }
  }
`;
const DayDiv = styled.div`
  & > button {
    width: 2rem;
  }
`;

const WriteBook = () => {
  const editorRef = useRef();
  const [editorLoded, setEditorLoded] = useState(false);
  const { CKEditor, ClassicEditor } = editorRef.current || {};

  const [bookName, setBookName] = useState('');
  const [serialDay, setSerialDay] = useState({
    sun: false,
    mon: false,
    tue: false,
    wed: false,
    thu: false,
    fri: false,
    sat: false,
  });
  const { sun, mon, tue, wed, thu, fri, sat } = serailDay;
  const [summaryText, setSummaryText] = useState('');

  useEffect(() => {
    editorRef.current = {
      CKEditor: require('@ckeditor/ckeditor5-react'),
      ClassicEditor: require('@ckeditor/ckeditor5-build-classic'),
    };
    setEditorLoded(true);
  }, []);

  const onCancle = useCallback(e => {
    Router.back();
  }, []);

  const onSubmitForm = useCallback(e => {
    e.preventDefault();
  }, []);

  const onChangeDay = useCallback(
    e => {
      setSerialDay({
        ...serialDay,
        [e.target.name]: !e.target.value,
      });
    },
    [serailDay],
  );

  return (
    <SWriteBookDiv>
      <Helmet>
        <title>새 작품</title>
        <style type="text/css">
          {`
            .ck-editor__editable { height: 500px;}
          `}
        </style>
      </Helmet>
      <SWriteBookTitle>새 연재작품 만들기</SWriteBookTitle>
      <FormBook onSubmit={onSubmitForm}>
        <ButtonDiv>
          <Button color="pink" onClick={onCancle}>
            취소
          </Button>
          <Button type="submit">만들기</Button>
        </ButtonDiv>
        <DivCard>
          <DivCardLeft>
            <label>제목</label>
          </DivCardLeft>
          <DivCardRight>
            <input type="text"></input>
          </DivCardRight>
        </DivCard>
        <DivCard>
          <DivCardLeft>
            <label>썸네일</label>
          </DivCardLeft>
          <DivCardRight></DivCardRight>
        </DivCard>
        <DivCard>
          <DivCardLeft>
            <label>연재요일</label>
          </DivCardLeft>
          <DivCardRight>
            <DayDiv>
              <Button name="sun" value={sun} onChange={onChangeDay}>
                일
              </Button>
              <Button name="mon" value={mon} onChange={onChangeDay}>
                월
              </Button>
              <Button name="tue" value={tue} onChange={onChangeDay}>
                화
              </Button>
              <Button name="wed" value={wed} onChange={onChangeDay}>
                수
              </Button>
              <Button name="thu" value={thu} onChange={onChangeDay}>
                목
              </Button>
              <Button name="fri" value={fri} onChange={onChangeDay}>
                금
              </Button>
              <Button name="sat" value={sat} onChange={onChangeDay}>
                토
              </Button>
            </DayDiv>
          </DivCardRight>
        </DivCard>
        <DivCard>
          <DivCardLeft></DivCardLeft>
          <DivCardRight></DivCardRight>
        </DivCard>
        {editorLoded ? (
          <CKEditor
            editor={ClassicEditor}
            data={summaryText}
            onChange={(event, editor) => {
              const data = editor.getData();
              setSummaryText(data);
            }}
          />
        ) : (
          <p>Editor Loding</p>
        )}
      </FormBook>
    </SWriteBookDiv>
  );
};

export default WriteBook;

