import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';

const Card = styled.div``;

const Thumbnail = styled.div``;

const CardContent = styled.div``;

const Title = styled.label``;

const Genre = styled.label``;

const Writer = styled.label``;

const CreatedDate = styled.label``;

const BookCard = ({ book, nickname }) => {
  const dispatch = useDispatch();

  return (
    <Card>
      <Thumbnail>{book.thumbnail}</Thumbnail>
      <CardContent>
        <Title>{book.name}</Title>
        {/* {book.BookGenre[0].name} */}
        {book.BookGenre.map((genre, i) => {
          <Genre key={i}>{genre.name}</Genre>;
        })}
        <Writer>{nickname}</Writer>
        <CreatedDate>{book.createdAt}</CreatedDate>
      </CardContent>
    </Card>
  );
};

BookCard.propTypes = {
  book: PropTypes.object.isRequired,
  nickname: PropTypes.string.isRequired,
};

export default BookCard;
