import React, { useCallback } from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';
import Router from 'next/router';
import moment from 'moment';

const Table = styled.table`
  border-collapse: separate;
  border-spacing: 1px;
  background: white;
  border-radius: 12px;
  overflow: hidden;
  margin: 1rem auto;
  width: 98%;

  & th {
    padding: 1rem;
    background: #148cff;
    color: white;
  }
  & td {
    padding: 1rem;
    border-bottom: 1px solid #7053c4;
    cursor: pointer;
  }

  & tr:hover {
    background: #495057;
    color: white;
  }
`;

const BookTable = ({ episode }) => {
  const goEpisode = useCallback(
    episodeId => e => {
      Router.push(
        {
          pathname: '/episode',
          query: { id: episodeId },
        },
        `/episode/${episodeId}`,
      );
    },
    [],
  );

  // 번호 제목 날짜 조회 추천
  return (
    <Table>
      <thead>
        <tr>
          <th>번호</th>
          <th>제목</th>
          <th>날짜</th>
          <th>조회</th>
          <th>추천</th>
        </tr>
      </thead>
      <tbody>
        {episode.map(v => {
          return (
            <tr key={+v.id} onClick={goEpisode(+v.id)}>
              <td>{v.id}</td>
              <td>{v.title}</td>
              <td>{moment(v.createdAt).format('YYYY.MM.DD')}</td>
              <td>{v.views}</td>
              <td>{v.recommends}</td>
            </tr>
          );
        })}
      </tbody>
    </Table>
  );
};

BookTable.propTypes = {
  episode: PropTypes.array.isRequired,
};

export default BookTable;
