import React from 'react';
import styled from 'styled-components';
import { LOAD_BOARDS_REQUEST } from '../reducers/board';
import { useSelector } from 'react-redux';
import BoardTable from '../components/BoardTable';
import Button from '../components/designs/Button';

const SBoard = styled.div`
  margin-top: 10px;
`;
const BoardDivTop = styled.div`
  display: flex;
`;

const SBoardTitle = styled.h1`
  display: inline-block;
  font-weight: bold;
  width: 200px;
`;
const SearchForm = styled.form`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  padding: 0px 10px;

  width: 99%;

  & a {
    color: white;
    height: 50px;
    padding-right: 10px;
    display: flex;
    align-items: center;
    justify-content: center;
  }
`;

const SearchInput = styled.input`
  margin-right: 10px;
  /* outline: none;
  border: none; */
  border: solid 2px #000000;
  border-radius: 4px;
  padding-left: 10px;

  height: 2.25rem;
  width: 10rem;
  font-size: 1.5rem;

  transition: 0.55s ease;

  & :hover {
    width: 30rem;
  }
  & :focus {
    width: 30rem;
  }
`;

const Board = () => {
  const { boards } = useSelector(state => state.board);

  return (
    <SBoard>
      <BoardDivTop>
        <SBoardTitle>추천 게시판</SBoardTitle>
        <SearchForm>
          <SearchInput></SearchInput>
          <Button>검색</Button>
        </SearchForm>
      </BoardDivTop>
      {boards && <BoardTable board={boards} />}
    </SBoard>
  );
};

Board.getInitialProps = async context => {
  context.store.dispatch({
    type: LOAD_BOARDS_REQUEST,
  });
};

export default Board;
