import React from 'react';
import styled from 'styled-components';

const StyledButton = styled.button`
  display: inline-block;
  outline: none;
  border: none;
  border-radius: 4px;
  color: white;
  font-weight: bold;
  cursor: pointer;
  padding-left: 1rem;
  padding-right: 1rem;

  height: 2.25rem;
  font-size: 1rem;

  ${props => {
    const selected = props.theme.pa;
  }}
  /* 
    blue: '#228be6 #339af0 #1c7ed6',
    gray: '#495057',
    pink: '#f06595' 
  */

  background: #148CFF;
  &:hover {
    background: #1e96ff;
  }
  &:active {
    background: #0a82ff;
  }

  & + & {
    margin-left: 1rem;
  }
`;

const Button = ({ children, ...rest }) => {
  return <StyledButton {...rest}>{children}</StyledButton>;
};

Button.defaultProps = {
  color: 'blue',
};

export default Button;
