import React from 'react';
import styled from 'styled-components';
import moment from 'moment';
import Link from 'next/link';

import { LOAD_BOOK_REQUEST } from '../reducers/book';
import { useSelector } from 'react-redux';

const SBookDiv = styled.div`
  width: 98%;
  margin: 10px 0px 0px 10px;
`;

const BookTopDiv = styled.div`
  display: inline-block;
  width: 100%;
  height: 15rem;
`;
const BookTopLeftDiv = styled.div`
  margin-top: 1rem;
  width: 15%;
  float: left;
`;
const BookTopRightDiv = styled.div`
  width: 75%;
  padding: 1.5rem;
  float: right;

  & > label {
    font-size: 1rem;
    display: block;
    margin-top: 0.5rem;
  }
`;
const Thumbnail = styled.img`
  height: 13rem;
  float: left;

  margin-top: 1rem;
  margin-left: 4%;
`;
const BookTitle = styled.h1`
  font-size: 1.5rem;
  font-weight: bold;
`;
const BookSummaryDiv = styled.div`
  margin: 2rem;
`;

const Book = () => {
  const { book } = useSelector(state => state.book);

  return (
    <SBookDiv>
      <BookTopDiv>
        <BookTopLeftDiv>
          <Thumbnail src={`http://localhost:5000/${book && book.thumbnail}`} />
        </BookTopLeftDiv>
        <BookTopRightDiv>
          <BookTitle>{book && book.name}</BookTitle>
          <label>{book && book.BookGenre[0].name}</label>
          <label>작가 {book && book.User.nickname}</label>
          <label>
            작품등록일{' '}
            {book && moment(book.createdAt).format('YY.MM.DD HH시 mm분')}
          </label>
          <label>
            최근등록일{' '}
            {book &&
              (book.Episodes[book.Episodes.length - 1]
                ? moment(
                    book.Episodes[book.Episodes.length - 1].createdAt,
                  ).format('YY.MM.DD HH시 mm분')
                : moment(book.createdAt).format('YY.MM.DD HH시 mm분'))}
          </label>
          <label>연재수 {book && book.Episodes.length} 화</label>
          <label>조회수</label>
          <label>추천수</label>
          <label>선호작수 {book && book.likersCount}</label>
        </BookTopRightDiv>
      </BookTopDiv>
      <BookSummaryDiv>{book && book.summary}</BookSummaryDiv>
    </SBookDiv>
  );
};

Book.getInitialProps = async context => {
  const bookId = context.query.bookid;

  context.store.dispatch({
    type: LOAD_BOOK_REQUEST,
    id: bookId,
  });
};

export default Book;
