import React, { useEffect, useCallback, useState } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import Router from 'next/router';
import { useSelector } from 'react-redux';

import { LOAD_USER_REQUEST } from '../reducers/user';
import Button from '../components/designs/Button';

const SProfileDiv = styled.div`
  width: 98%;
  margin-top: 10px;
`;
const ProfileTitle = styled.h1`
  display: inline-block;
  font-weight: bold;
  margin-left: 10px;
  width: 100%;
`;
const InfoCardWrap = styled.div`
  margin-top: 2rem;
  margin-left: 1.5rem;
  border: solid 1px #495057;
  border-radius: 6px;
  width: 70%;
`;
const InfoCardDiv = styled.div`
  display: flex;
  align-items: center;
  margin: 1rem;
  height: 4rem;
`;
const InfoLeftDiv = styled.div`
  flex: 1;
`;
const InfoRightDiv = styled.div`
  flex: 3;
  display: flex;
  & > input {
    width: 100%;
    height: 2rem;
    font-size: 1rem;
    line-height: 2rem;
    padding-left: 10px;

    border: solid 2px #495057;
    border-radius: 4px;

    transition: 0.55s ease;

    & :hover {
      border: solid 2px #148cff;
    }
    & :focus {
      border: solid 2px #148cff;
    }
  }
`;

const CheckButton = styled(Button)`
  width: 8rem;
  height: 2rem;
  margin-left: 10px;
`;

const ButtonDiv = styled.div`
  margin: 1rem;
  height: 4rem;
`;

const SButton = styled(Button)`
  display: block;
  width: 8rem;
  margin-top: 1rem;

  float: right;
`;

const Profile = ({ id }) => {
  const { me } = useSelector(state => state.user);

  const [inputs, setInputs] = useState({
    userId: '',
    userPassword: '',
    userNickname: '',
  });

  const { userId, userPassword, userNickname } = inputs;

  const onChangeInputs = useCallback(
    e => {
      setInputs({
        ...inputs,
        [e.target.name]: e.target.value,
      });
    },
    [inputs],
  );

  useEffect(
    e => {
      if (!(me && me.id)) {
        alert('로그인 하지 않은 사용자는 접근 할 수 없습니다.');
        Router.push('/');
        return;
      }
      if ((me && me.userId) !== id) {
        alert('잘 못된 접근입니다.');
        Router.push('/');
        return;
      }
    },
    [me && me.userId, id],
  );

  const onUpdatePassword = useCallback(e => {}, []);
  const onUpdateProfile = useCallback(e => {}, []);
  return (
    <SProfileDiv>
      <ProfileTitle>{me && me.nickname} 님 프로필 수정</ProfileTitle>
      <InfoCardWrap>
        <InfoCardDiv>
          <InfoLeftDiv>
            <label>아이디</label>
          </InfoLeftDiv>
          <InfoRightDiv>
            <input
              name={'userId'}
              value={userId}
              placeholder={me && me.userId}
              onChange={onChangeInputs}
            />
            <CheckButton>중복확인</CheckButton>
          </InfoRightDiv>
        </InfoCardDiv>
        <InfoCardDiv>
          <InfoLeftDiv>
            <label>비밀번호</label>
          </InfoLeftDiv>
          <InfoRightDiv>
            <Button size={'middle'} onClick={onUpdatePassword}>
              비밀번호 변경
            </Button>
          </InfoRightDiv>
        </InfoCardDiv>
        <InfoCardDiv>
          <InfoLeftDiv>
            <label>닉네임</label>
          </InfoLeftDiv>
          <InfoRightDiv>
            <input value={me && me.nickname} />
            <CheckButton>중복확인</CheckButton>
          </InfoRightDiv>
        </InfoCardDiv>
        <ButtonDiv>
          <SButton onClick={onUpdateProfile}>수정하기</SButton>
        </ButtonDiv>
      </InfoCardWrap>
      {/* <table>
        <tr>
          <td>아이디</td>
          <td>{me && me.userId}</td>
        </tr>
        <tr>
          <td>닉네임</td>
          <td>{me && me.nickname}</td>
        </tr>
        <tr>
          <td>연재한 글 수</td>
          <td>{me && me.Books.length}</td>
        </tr>
        <tr>
          <td>선호작 수</td>
          <td>{me && me.LikingBook.length}</td>
        </tr>
        <tr>
          <td>선호작가 수</td>
          <td>{me && me.LikingUser.length}</td>
        </tr>
      </table> */}
    </SProfileDiv>
  );
};

Profile.propTypes = {
  id: PropTypes.string.isRequired,
};

Profile.getInitialProps = async context => {
  const id = context.query.id;
  return { id };
};

export default Profile;
