import { all, call, fork, throttle, takeLatest, put } from 'redux-saga/effects';
import axios from 'axios';
import {
  LOAD_BOARDS_REQUEST,
  LOAD_BOARDS_SUCCESS,
  LOAD_BOARDS_FAILURE,
} from '../reducers/board';

function* watchLoadBords() {
  yield throttle(1000, LOAD_BOARDS_REQUEST, function*(action) {
    try {
      console.log(`
      -------------------------
      -------------------------
      -------------------------
      ---- watchLoadBoards ----
      -------------------------
      -------------------------
      -------------------------
      `);
      const result = call((lastId = 0, limit = 10) => {
        const url = `/boards?lastId=${lastId}&limit=${limit}`;
        console.log(`
      -------------------------
      -------------------------
      -------------------------
      ---- ${url} ----
      -------------------------
      -------------------------
      -------------------------
      `);
        return axios.get(url);
      }, action.lastId);
      yield put({
        type: LOAD_BOARDS_SUCCESS,
        data: result.data,
      });
    } catch (e) {
      console.log(e);
      yield put({
        type: LOAD_BOARDS_FAILURE,
        error: e,
      });
    }
  });
}

export default function* bookSaga() {
  yield all([fork(watchLoadBords)]);
}
