import React, { useCallback } from 'react';
import styled from 'styled-components';
import moment from 'moment';
import Link from 'next/link';
import { useSelector } from 'react-redux';
import Router from 'next/router';

import { LOAD_BOOK_REQUEST } from '../reducers/book';

const SBookDiv = styled.div`
  width: 98%;
  margin: 30px 0px 0px 10px;
`;

const BookTopDiv = styled.div`
  display: inline-block;
  width: 100%;
  height: 16rem;

  border-bottom: solid 2px #495057;
`;
const BookTopLeftDiv = styled.div`
  width: 15%;
  float: left;
`;
const BookTopRightDiv = styled.div`
  width: 80%;
  padding: 1.5rem;
  float: right;

  border: solid 2px #495057;

  & > label {
    font-size: 1rem;
    display: block;
    margin-top: 1rem;

    & > a {
      cursor: pointer;
    }
  }

  & > div {
    display: flex;

    & > label {
      font-size: 1rem;
      display: block;
      margin-top: 1rem;
    }

    label + label {
      margin-left: 1rem;
    }
  }
`;
const Thumbnail = styled.img`
  height: 13rem;
  display: block;
  margin: 1rem auto;
`;
const BookTitle = styled.h1`
  font-size: 1.5rem;
  font-weight: bold;
`;

const BookSummaryDiv = styled.div`
  margin-top: 1.5rem;
  margin-bottom: 1.5rem;
  padding-left: 1.5rem;
  padding-right: 1.5rem;
  height: 7rem;
  width: 100%;
  border-bottom: solid 2px #495057;
`;

const Book = () => {
  const { book } = useSelector(state => state.book);

  const onGoWriter = useCallback(
    e => {
      Router.push(
        { pathname: '/librery', query: { id: book.User.userId } },
        `/librery/${book.User.userId}`,
      );
    },
    [book && book.User.id],
  );
  return (
    <SBookDiv>
      <BookTopDiv>
        <BookTopLeftDiv>
          <Thumbnail src={`http://localhost:5000/${book && book.thumbnail}`} />
        </BookTopLeftDiv>
        <BookTopRightDiv>
          <BookTitle>{book && book.name}</BookTitle>
          <label>{book && book.BookGenre[0].name}</label>
          <label>
            작가 <a onClick={onGoWriter}>{book && book.User.nickname}</a>
          </label>
          <div>
            <label>
              작품등록일{' '}
              {book && moment(book.createdAt).format('YY.MM.DD HH시 mm분')}
            </label>
            <label>
              최근등록일{' '}
              {book &&
                (book.Episodes[book.Episodes.length - 1]
                  ? moment(
                      book.Episodes[book.Episodes.length - 1].createdAt,
                    ).format('YY.MM.DD HH시 mm분')
                  : moment(book.createdAt).format('YY.MM.DD HH시 mm분'))}
            </label>
          </div>
          <div>
            <label>연재수 {book && book.Episodes.length} 화</label>
            <label>
              조회수{' '}
              {book && book.Episodes.reduce((acc, val) => acc + val.views, 0)}
            </label>
          </div>
          <div>
            <label>
              추천수{' '}
              {/* {book && book.Episodes.reduce((acc, val) => acc.val.recomends, 0)} */}
            </label>
            <label>선호작수 {book && book.likersCount}</label>
          </div>
        </BookTopRightDiv>
      </BookTopDiv>
      <BookSummaryDiv
        dangerouslySetInnerHTML={{ __html: book && book.summary }}
      />
    </SBookDiv>
  );
};

Book.getInitialProps = async context => {
  const bookId = context.query.bookid;

  context.store.dispatch({
    type: LOAD_BOOK_REQUEST,
    id: bookId,
  });
};

export default Book;
